import {cards} from "../../data/cards";
import Card from "../../components/cards/Card";


const NUMCARDS = 10;
const NUMDECK = 78;

/** will return a random integer between 1 and 78 (number of tarot cards) */
function getRandomInt() {
    return Math.floor(Math.random() * Math.floor(NUMDECK));
}

function getRandomCards() {
    let cardList = [];
    const cardsArr = Array.from(cards);

    while(cardList.length < NUMCARDS) {
        const index = getRandomInt();
        const cardToAdd = cardsArr[index];
        if(! cardList.find((someCard)=> someCard === cardToAdd)) {
            cardList.push(cardToAdd);
        }

    }
    return cardList;
}


// let cx = classNames.bind(styles);

class CelticCross extends React.Component {
    constructor(props) {
      super(props);
      this.state = {cards: []};
    }

    componentDidMount() {
        const res = getRandomCards();
        console.log(res);

        this.setState({cards: res});
    }

    render() {

      return (
        <div>
            <ul>
                {this.state.cards.map((card,index) => (
                    <li id={index+1} className="back" key={card.name}>
                        <span>{card.name}</span>
                        <span className="cardNumLabel">{index + 1}</span>
                    </li>
                ))}
            </ul>
            <style jsx>{`

        ul {
          padding: 0;
        }

        li#1 {
            float: left;
        }

        li#2 {
            float: left;
            position: absolute;
            transform: rotate(90deg);
        }

        .back {
          background: maroon;
          padding: 5px;
          margin: 5px;
          height: 150px;
          width: 110px;
          list-style:none;
        }

        .cardNumLabel {
            display: block;
            margin: 5px;
            font-weight:bold;

        }
      `}</style>
         </div>

      );
    }
  }

  export default CelticCross;
