import cards from "../../data/cards";

/** will return a random integer between 1 and 78 (number of tarot cards) */
function getRandomInt() {
    return Math.random * Math.floor(78);
}

function getRandomCards() {
    let cardList = [];
    const cardsArr = Array.from(cards.json());

    while(cardList.length <= 5) {
        const cardToAdd = cardsArr[getRandomInt()];
        if(!cardList.find(cardToAdd)) {
            cardList.push(cardToAdd);
        }

    }
    return cardList;
}

const CelticCross = ()=> {

    return (
        <div>
            here is a gameboard +.
        </div>
    );

}

CelticCross.getInitialProps = async function(context) {
    const { id } = context.query;
    const res = await fetch(`https://api.tvmaze.com/shows/${id}`);
    const show = await res.json();

    console.log(`Fetched show: ${show.name}`);

    return { show };
  };

export default CelticCross;
