import {cards} from "../../data/cards";
import Card from "../../components/cards/Card";


const NUMCARDS = 10;
const NUMDECK = 78;

/** will return a random integer between 1 and 78 (number of tarot cards) */
function getRandomInt() {
    return Math.floor(Math.random() * Math.floor(NUMDECK));
}

function getRandomCards() {
    let cardList = [];
    const cardsArr = Array.from(cards);

    while(cardList.length < NUMCARDS) {
        const index = getRandomInt();
        const cardToAdd = cardsArr[index];
        if(! cardList.find((someCard)=> someCard === cardToAdd)) {
            cardList.push(cardToAdd);
        }

    }
    return cardList;
}


// let cx = classNames.bind(styles);

class CelticCross extends React.Component {
    constructor(props) {
      super(props);
      this.state = {
          cards: [],
          flipIndex: 0
        };
    }

    componentDidMount() {
        const res = getRandomCards();
        console.log(res);

        this.setState({cards: res});

        document.onclick(() => {
            this.setState({flipIndex: flipIndex + 1});
            document.getElementsByClassName('card-'+flipIndex)[0].style.display = "inherit";
        });
    }

    render() {

      return (
        <div>
            <ul>
                {this.state.cards.map((card,index) => (
                    <li className={`face card-${index+1}`} key={card.name}>
                        <span>{card.name}</span>
                        <span className="cardNumLabel">{index + 1}</span>
                    </li>
                ))}
            </ul>
            <style jsx>{`

        ul {
          padding: 0;
          position: relative;
          height: 100vh;
        }

        .card-1 {
            position: absolute;
            top: 200px;
            left: 150px;
        }

        .card-2 {
            float: left;
            position: absolute;
            transform: rotate(90deg);
            top: 205px;
            left: 150px;
        }

        .card-3 {
            position: absolute;
            top: 0;
            left: 150px;
        }

        .card-4 {
            position: absolute;
            top: 400px;
            left: 150px;
        }

        .card-5{
            position: absolute;
            top: 200px;
            left: 300px;
        }

        .card-6 {
            position: absolute;
            top: 200px;
            left: 0;
        }
        .card-7 {
            position: absolute;
            bottom: 40px;
            right: 0;
        }
        .card-8 {
            position: absolute;
            right: 0;
            bottom: 200px;
        }
        .card-9 {
            position: absolute;
            top: 160px;
            right: 0;
        }
        .card-10 {
            position: absolute;
            top: 0;
            right: 0;
        }

        .flipped {
            display: inherit;
        }

        .face {
          background: maroon;
          display: none;
          padding: 5px;
          margin: auto;
          height: 130px;
          width: 90px;
          list-style:none;
          text-align: center;
          z-index:1;
        }

        .face:hover {
            z-index: 2;
        }

        .cardNumLabel {
            display: block;
            margin: 5px;
            font-weight:bold;

        }
      `}</style>
         </div>

      );
    }
  }

  export default CelticCross;
